import "./App.css";
import React, { useState, useEffect } from "react";
import { sendMessage, publicKey, messaging } from "./firebaseInit";
import { getToken } from "firebase/messaging";
import TextField from '@mui/material/TextField';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import axios from 'axios';


function App() {
  console.log(messaging,"messaging -------");
  const [show, setShow] = useState(false);
  const [notification, setNotification] = useState({ title: "Titulo", body: "que onda pa!" });
  const [dataObj, setData] = useState({
    titulo: "",
    mensaje: "",
    token: "",
  })

  console.log(show, notification);

  useEffect(() => {

    getToken(messaging, { vapidKey: publicKey }).then((currentToken) => {
      if (currentToken) {
        let data = Array.from(dataObj)
        data.token = currentToken
        setData(data)
        // Send the token to your server and update the UI if necessary
        // ...
      } else {
        // Show permission request UI
        console.log('No registration token available. Request permission to generate one.');
        // ...
      }
    }).catch((err) => {
      console.log('An error occurred while retrieving token. ', err);
      // ...
    });


  }, [])

  const handleChange = (e) => {
    setData({
      ...dataObj,
      [e.target.name]: e.target.value
    })
  }

  const test = () => {
    console.log(dataObj, "Calando pa");
    axios.post('/localhost/tarea/create', dataObj)
            .then((resp) => {
              setMessages(resp.data.messages.concat(messages));
              actions.setSubmitting(false);
              toast.success('Submitted succesfully');
            })
            .catch((err) => {
              console.log(err);
              toast.error('There was an error saving the message');
            });
    sendMessage(dataObj)
  }

  return (
    <div className="App">
      <>
        <Grid item xs={12} style={{ marginTop: '20px', marginLeft: '10px', }} >
          <Grid container spacing={2}>
            <Grid item xs={6} style={{ marginTop: '20px', marginLeft: '10px' }} >
              <Grid container spacing={2}>
                <Grid item xs={6} >
                  <TextField id="outlined-basic" label="Titulo" name='titulo' onChange={handleChange} placeholder='Titulo' value={dataObj.titulo} variant="outlined" fullWidth />
                </Grid>
                <Grid item xs={6} >
                  <TextField id="outlined-basic" label="Topic" variant="outlined" fullWidth />
                </Grid>
                <Grid item xs={12} >
                  <TextField id="outlined-basic" label="Mensaje" name='mensaje' onChange={handleChange} placeholder='Mensaje' value={dataObj.mensaje} variant="outlined" fullWidth />
                </Grid>
                <Grid item xs={12} >
                  <TextField id="outlined-basic" label="Url" variant="outlined" fullWidth />
                </Grid>
                <Grid item xs={12} >
                  <TextField id="outlined-basic" label="Url imagen" variant="outlined" fullWidth />
                </Grid>
                <Grid item xs={6} >
                  <TextField id="outlined-basic" label="CampaÃ±a" variant="outlined" fullWidth />
                </Grid>
                <Grid item xs={3} >
                  <TextField id="outlined-basic" label="Fuente" variant="outlined" fullWidth />
                </Grid>
                <Grid item xs={3} >
                  <TextField id="outlined-basic" label="Medio" variant="outlined" fullWidth />
                </Grid>
                <Grid item xs={6} >
                  <TextField id="outlined-basic" label="Fecha programada" variant="outlined" fullWidth />
                </Grid>
                <Grid item xs={6} >
                  <TextField id="outlined-basic" label="Fecha enviada" variant="outlined" fullWidth />
                </Grid>
                <Grid item xs={8} >
                  <TextField id="outlined-basic" label="Token" variant="outlined" fullWidth />
                </Grid>
                <Grid item xs={2} style={{ marginTop: '18px' }}>
                  <Button variant="outlined" onClick={test}>TEST</Button>
                </Grid>
              </Grid>
            </Grid>
            <Grid item xs={4} style={{ marginTop: '20px', marginLeft: '10px', border: '1px solid blue' }} >
              <h4>Push Notification</h4>
            </Grid>
          </Grid>
        </Grid>
      </>;
    </div>
  );
}

export default App;
